CREATE TABLE eventos (_id INTEGER PRIMARY KEY AUTOINCREMENT, tipo INTEGER NOT NULL, titulo TEXT NOT NULL, descripcion TEXT, fecha DATE, notificar INTEGER NOT NULL, finalizado BOOLEAN, repeat INTEGER NOT NULL DEFAULT 0, sun INTEGER NOT NULL DEFAULT 0, mon INTEGER NOT NULL DEFAULT 0, tue INTEGER NOT NULL DEFAULT 0, wed INTEGER NOT NULL DEFAULT 0, thu INTEGER NOT NULL DEFAULT 0, fri INTEGER NOT NULL DEFAULT 0, sat INTEGER NOT NULL DEFAULT 0);
